mapper.select:
SELECT photo_url
FROM facility_photo
WHERE facility_id = #{facilityId}

SELECT facility_tag
FROM facility_tag
WHERE facility_id = #{facilityId}

SELECT f.facility_id, f.facility_name, f.facility_address_location, f.facility_address_city, f.facility_charge, fp.photo_url FROM facility f LEFT JOIN facility_photo fp ON f.facility_id = fp.facility_id AND fp.is_thumbnail = 1 WHERE f.facility_type = '실버타운'
AND f.facility_address_location = #{location}
AND f.facility_address_city = #{city}
AND f.facility_theme = #{theme}
AND f.facility_id IN ( SELECT facility_id FROM facility_tag WHERE category = '주거형태' AND facility_tag IN
#{item}
)
AND f.facility_id IN ( SELECT facility_id FROM facility_tag WHERE category = '시설' AND facility_tag IN
#{item}
)
AND f.facility_id IN ( SELECT facility_id FROM facility_tag WHERE category = '주변환경' AND facility_tag IN
#{item}
)
AND f.facility_id IN ( SELECT facility_id FROM facility_tag WHERE category = '기타' AND facility_tag IN
#{item}
)
ORDER BY f.facility_id DESC
