mapper.select:
SELECT username
FROM
dv_users
WHERE name = #{name}
AND phone = #{phone}

SELECT *
FROM dv_users
WHERE name = #{name}
AND username = #{username}
AND phone = #{phone}

mapper.update:
UPDATE dv_users
SET password = #{password}
WHERE
username = #{username}

mapper.select:
SELECT password
FROM dv_users
WHERE username = #{username}
